mapper.select:
select COUNT(*)
from `order`
where shop_id = #{shopId}
  and DATEDIFF(order_time, NOW()) >= -7;

select o.order_id,o.order_time,o.order_amount,o.order_state,odn.goods_name,odn.goods_quantity,odn.goods_amount
from `order` as o,`orderNote` as odn where o.order_id = odn.order_id

    and o.order_state = #{orderState}
